app.get("/api/items", (req, res) => {
    const q = req.query.q; // search term from URL
    if (q) {
        const filteredItems = product.filter(item =>
            item.name.toLowerCase().includes(q.toLowerCase())
        );
        res.json({ data: filteredItems });
    } else {
        res.json({ data: product }); // return all items if no search term
    }
});
